mapper.select:
select

from o_reg_record
where user_id = #{userId,jdbcType=VARCHAR}
and op_id = #{opId,jdbcType=INTEGER}

mapper.delete:
DELETE FROM o_reg_record
WHERE user_id = #{userId,jdbcType=VARCHAR}
      AND op_id = #{opId,jdbcType=INTEGER}

mapper.select:
SELECT sum(c.acreage) AS sum FROM o_reg_record a, o_owner b, o_owner_org c, o_op_info d WHERE a.op_id = d.id AND a.user_id = b.id AND b.id = c.user_id
AND a.info_id = #{infoId}
AND a.op_id = #{opId}
AND c.org_id in (SELECT id from o_org WHERE id=d.org_id OR p_id=d.org_id)

SELECT SUM(c.acreage) AS acreage, a.choice AS choice FROM o_reg_record a, o_owner b, o_owner_org c, o_op_info d WHERE a.op_id = d.id and a.user_id = b.id AND b.id = c.user_id AND c.org_id in (SELECT id from o_org WHERE id=d.org_id OR p_id=d.org_id)
AND a.info_id = #{infoId}
AND a.op_id = #{opId}
GROUP BY a.choice
